mapper.select:
select * from admin

    and id = #{id}
    and username = #{username}
    and password = #{password}
    and name = #{name}
    and status = #{status}

mapper.update:
update admin

    , username = #{username}
    , password = #{password}
    , name = #{name}
    , status = #{status}
    , update_time = #{updateTime}

where id = #{id}
